mapper.insert:
		insert into show_tb(
			title, content, start_date, end_date, price, show_status, show_img, show_type_id1, hall_tb_id	
		)
	  	values(
	  		#{title}, #{content}, #{startDate}, #{endDate}, #{price}, '승인 요청', #{showImg}, #{showTypeId1}, #{hallTbId}
  		)

		insert into show_time_tb(
			start_time, show_id	
		)
	  	values(
	  		#{startTime}, (select max(id) from show_tb)
  		)

		insert into rental_tb(
			show_tb_id, hall_tb_id, user_tb_id, rental_start_time, rental_end_time	
		)
	  	values(
	  		(select max(id) from show_tb), #{hallTbId}, #{userTbId}, #{rentalStartTime}, #{rentalEndTime}
  		)
