mapper.select:
select * from show_tb where start_date >= #{startDate} and not start_date > #{endDate}
union
select * from show_tb where #{endDate} >= end_date and not #{startDate} > end_date 
union
select * from show_tb where #{startDate} >= start_date and end_date >= #{endDate}
order by end_date

mapper.insert:
		insert into show_tb(
			title, content, start_date, end_date, price, show_status, show_img, show_type_id1, hall_tb_id	
		)
	  	values(
	  		#{title}, #{content}, #{startDate}, #{endDate}, #{price}, '승인 요청', #{showImg}, #{showTypeId1}, #{hallTbId}
  		)
